-- ----------------------------
-- Table structure for `Article`
-- ----------------------------
DROP TABLE IF EXISTS `Article`;
CREATE TABLE `Article` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `title` varchar(256) DEFAULT NULL COMMENT '标题',
  `abstract` varchar(512) DEFAULT NULL COMMENT '摘要',
  `content` text COMMENT '内容',
  `tag` varchar(128) DEFAULT NULL COMMENT '标签',
  `logo` varchar(256) DEFAULT NULL COMMENT '列表logo',
  `logo2` varchar(256) DEFAULT NULL COMMENT '列表logo2',
  `categoryId` bigint(20) DEFAULT NULL COMMENT '类别ID',
  `source` varchar(512) DEFAULT NULL COMMENT '来源',
  `views` int(11) DEFAULT '0' COMMENT '浏览器',
  `reviews` int(11) DEFAULT '0' COMMENT '评论数',
  `editor` varchar(255) DEFAULT NULL COMMENT '编辑员',
  `listType` int(11) DEFAULT '1' COMMENT '列表显示类型',
  `isHeadlines` bit(1) DEFAULT b'0' COMMENT '是否为头条',
  `isPlacedHome` bit(1) DEFAULT b'0' COMMENT '是否置于首页',
  `isFeatured` bit(1) DEFAULT b'0' COMMENT '是否特别推荐',
  `isPublish` bit(1) DEFAULT b'0' COMMENT '是否发布',
  `isDelete` bit(1) DEFAULT b'0' COMMENT '是否删除',
  `createTime` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `modifyTime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=17 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of Article
-- ----------------------------
INSERT INTO `Article` VALUES ('1', '全世界唯一一只白色座头鲸', '米伽罗是世界上唯一的白色雄性座头鲸。米伽罗是澳洲土著语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下', '2011年10月，一头通体全白的座头鲸在澳大利亚大堡礁附近出现，出生数周，大概有12英尺(约3.65米)长。这种纯白色座头鲸估计在澳大利亚东海岸水域仅有10到15头，出现几率仅为千分之一。目击者韦恩·费文思看见了座头鲸浮出水面的瞬间。“这是一辈子也轮不上的运气。”费文思说，“我们当时正在漂流，突然，我看见一只白色的小鲸鱼出现在海面上。我当时简直不敢相信自己的眼睛。”', '世界唯一,座头鲸,动物,奇闻怪事', 'upload/20151123/logo-22-1-1.jpg', '', '22', '米尔网', '34', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-23 16:42:36', '2015-11-23 23:02:02');
INSERT INTO `Article` VALUES ('2', '百岁婆婆返老还童', '104岁老人长黑发换新牙，相信你一定觉得不可思议。湖北随县城关104岁婆婆杨传玉身体健康，不但生活自理，还能帮家人做些家务事。让人惊奇的是，杨传玉100岁以后竟然长了新牙和黑头发，而且皮肤不松弛很有弹性，当地人称这位百岁婆婆返老还童。', '104岁老人长黑发换新牙，相信你一定觉得不可思议。湖北随县城关104岁婆婆杨传玉身体健康，不但生活自理，还能帮家人做些家务事。让人惊奇的是，杨传玉100岁以后竟然长了新牙和黑头发，而且皮肤不松弛很有弹性，当地人称这位百岁婆婆返老还童。更神奇的是，杨传玉在50多时做过一次体检，被查出有严重心脏病，随时可能死亡。这么多年过去了，老人一次都没犯过病。', '返老还童,奇闻怪事', 'upload/20151123/logo-22-2-1.jpg', null, '22', '中国网', '7', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-23 22:46:01', '2015-11-23 22:47:11');
INSERT INTO `Article` VALUES ('3', '三国美女之洛神', '洛神甄宓是中山无极(今河北省定元县)人，祖上出了一位名人，叫做甄邯，在西汉末年和王莽新朝时做过太保、大司马。甄宓一出生，就带有传奇色彩。', '洛神甄宓是中山无极(今河北省定元县)人，祖上出了一位名人，叫做甄邯，在西汉末年和王莽新朝时做过太保、大司马。甄宓一出生，就带有传奇色彩。\r\n据说，其母怀她时，每每睡觉，总梦见一仙人，手持玉如意，站在旁边守护着她。临产时，那仙人又入得房中，将一件玉衣盖在她身上。紧接着，甄宓就呱呱落地。\r\n在那个时代，这种虚无缥缈的征兆总是引得人们浮想联翩。', '美女,历史趣闻', 'upload/20151123/logo-27-3-1.jpg', '', '27', '49363', '8', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-23 23:03:08', '2015-11-23 23:03:10');
INSERT INTO `Article` VALUES ('4', '古代选妃女子竟要被太监摸遍', '有明一代，为类避免前代的外戚之祸，妃子都选自民间，不重视门第，重视品行，妃子都出身于选秀女，不允许大臣推荐。明朝早期，还实行严格的殉葬制度，皇帝死后妃子要殉葬，其惨烈程度历朝历代都很罕见，但即便如此，民间女性也不能回避选妃。', '有明一代，为类避免前代的外戚之祸，妃子都选自民间，不重视门第，重视品行，妃子都出身于选秀女，不允许大臣推荐。明朝早期，还实行严格的殉葬制度，皇帝死后妃子要殉葬，其惨烈程度历朝历代都很罕见，但即便如此，民间女性也不能回避选妃。', '历史趣闻', 'upload/20151123/logo-27-4-1.jpg', null, '27', '49363', '17', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-23 23:39:55', '2015-11-25 16:07:37');
INSERT INTO `Article` VALUES ('5', '最大的阴茎', '最大的阴茎有多大呢？对于男性最隐私的部位，一般人肯定是不愿意透露的，本文将为大家4个拥有最大的阴茎的男人，分别是长达56厘米的非洲象人族，长达28.5厘米的俄罗斯传奇修道士拉斯普京，长达34厘米的美国男子Jonah Falcon和可以用自己阴茎让车轮转的中国秦朝的嫪毐，耐心看完，一个比一个精彩。', '最大的阴茎有多大呢？对于男性最隐私的部位，一般人肯定是不愿意透露的，本文将为大家4个拥有最大的阴茎的男人，分别是长达56厘米的非洲象人族，长达28.5厘米的俄罗斯传奇修道士拉斯普京，长达34厘米的美国男子Jonah Falcon和可以用自己阴茎让车轮转的中国秦朝的嫪毐，耐心看完，一个比一个精彩。', '人类之最,最大,阴茎', 'upload/20151124/logo-4-5-1.jpg', null, '4', '世界之最', '10', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-24 22:26:25', '2015-11-24 23:11:38');
INSERT INTO `Article` VALUES ('6', '历史上寿命最长的皇帝', '历史上寿命最长的皇帝是哪个皇帝呢？很多人认为是89岁的乾隆皇帝，严格意义上说是没错的，但如果范围放广一点，就不是乾隆帝了，寿命最长的皇帝应该是活了103岁的南越武帝赵佗，而寿命超过80岁的皇帝有5位，他们分别是萧衍、武则天、赵构、忽必烈、乾隆。', '历史上寿命最长的皇帝是哪个皇帝呢？很多人认为是89岁的乾隆皇帝，严格意义上说是没错的，但如果范围放广一点，就不是乾隆帝了，寿命最长的皇帝应该是活了103岁的南越武帝赵佗，而寿命超过80岁的皇帝有5位，他们分别是萧衍、武则天、赵构、忽必烈、乾隆。', '人类之最,最长寿,皇帝', 'upload/20151124/logo-4-6-1.jpg', null, '4', '世界之最', '9', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-24 22:42:33', '2015-11-24 23:11:41');
INSERT INTO `Article` VALUES ('7', '世界上最大的胸部', '世界上最大的胸部是谁？现代社会的女人，腰越来越细，胸部越来越大，营养好是一个原因，更大的原因是可以人工隆胸了，世界上最大的胸部是一名来自美国的脱衣舞娘玛茜·蒙德斯，他的胸部重达36斤，已经获得吉尼斯记录认证，看一起看看有多大。', '世界上最大的胸部是谁？现代社会的女人，腰越来越细，胸部越来越大，营养好是一个原因，更大的原因是可以人工隆胸了，世界上最大的胸部是一名来自美国的脱衣舞娘玛茜·蒙德斯，他的胸部重达36斤，已经获得吉尼斯记录认证，看一起看看有多大。', '人类之最,最大,胸部', 'upload/20151124/logo-4-7-1.jpg', null, '4', '世界之最', '4', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-24 23:11:29', '2015-11-24 23:11:37');
INSERT INTO `Article` VALUES ('8', '世界上最小的婴儿', '正常情况下，婴儿应该是怀胎十月，可是世界上最小的婴儿，只在妈妈肚子里待了6个月；这位来自山东菏泽的妈妈，生了5胞胎，最小的女儿不幸夭折，剩下四个婴儿中，最小的婴儿仅仅250克，打破了此前由美国婴儿阿米利娅·泰勒保持的280克的世界上最小的婴儿记录。', '正常情况下，婴儿应该是怀胎十月，可是世界上最小的婴儿，只在妈妈肚子里待了6个月；这位来自山东菏泽的妈妈，生了5胞胎，最小的女儿不幸夭折，剩下四个婴儿中，最小的婴儿仅仅250克，打破了此前由美国婴儿阿米利娅·泰勒保持的280克的世界上最小的婴儿记录。', '人类之最,最小,婴儿', 'upload/20151124/logo-4-8-1.jpg', null, '4', '世界之最', '10', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-24 23:54:30', '2015-11-24 23:54:32');
INSERT INTO `Article` VALUES ('9', '世界上最美的十种花', '相信每一个人都喜欢花吧，而且都会追求世界上最美的花，但是世界上花的种类非常多，那些才是世界上最美丽的花被？国外一个花卉组织目前评选出了世界十大最美的花，跟着小编一起瞻仰，看是否有你喜欢的。', '相信每一个人都喜欢花吧，而且都会追求世界上最美的花，但是世界上花的种类非常多，那些才是世界上最美丽的花被？国外一个花卉组织目前评选出了世界十大最美的花，跟着小编一起瞻仰，看是否有你喜欢的。', '植物之最,最美,花', 'upload/20151125/logo-3-9-1.jpg', null, '3', '世界之最', '13', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-25 15:04:48', '2015-11-25 15:04:50');
INSERT INTO `Article` VALUES ('10', '世界上最大的花', '不要被电影中仙境里的超大花当真了，现实世界里是没有那么大的花儿的，那么世界上最大的花是什么花？那就是鼎鼎有名的大王花，也是传说中的食人花，其花瓣直径可以达到1.4米，花骨朵中间甚至可以装下一个人，更奇怪的是大王花发散的是臭味。', '不要被电影中仙境里的超大花当真了，现实世界里是没有那么大的花儿的，那么世界上最大的花是什么花？那就是鼎鼎有名的大王花，也是传说中的食人花，其花瓣直径可以达到1.4米，花骨朵中间甚至可以装下一个人，更奇怪的是大王花发散的是臭味。', '植物之最,最大,花', 'upload/20151125/text-3-10-1.jpg', null, '3', '世界之最', '6', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-25 15:38:50', '2015-11-25 15:38:51');
INSERT INTO `Article` VALUES ('11', '世界上最阴险的吃人植物', '只听说过 蛇吃人 的事情，但还从来没听过 植物 吃人的，虽然世界上有食人花，不过食人花吃人大多的都是传说，并没有真凭实据的发生过，而今天要介绍的就是世界上最阴险的一种吃人植物，日轮花，它可以帮助它的党羽蜘蛛把人咬死，来看看是咋回事。', '只听说过 蛇吃人 的事情，但还从来没听过 植物 吃人的，虽然世界上有食人花，不过食人花吃人大多的都是传说，并没有真凭实据的发生过，而今天要介绍的就是世界上最阴险的一种吃人植物，日轮花，它可以帮助它的党羽蜘蛛把人咬死，来看看是咋回事。', '植物之最,最阴险,吃人,花', 'upload/20151125/text-3-11-1.jpg', null, '3', '世界之最', '8', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-25 16:07:40', '2015-11-25 16:07:42');
INSERT INTO `Article` VALUES ('12', '世界上最大的辣椒', '世界上最辣的辣椒 是英国多西特郡出产的一种叫“纳加”红椒。而美国北卡罗莱纳州一位名叫瑟福的农民，培育了一只长达34.4厘米的辣椒。可以称得上是世界上最大的辣椒，因为没有过多的图片外漏，所以下面一起来看看那些关于辣椒的恐怖场景吧！', '世界上最辣的辣椒 是英国多西特郡出产的一种叫“纳加”红椒。而美国北卡罗莱纳州一位名叫瑟福的农民，培育了一只长达34.4厘米的辣椒。可以称得上是世界上最大的辣椒，因为没有过多的图片外漏，所以下面一起来看看那些关于辣椒的恐怖场景吧！', '植物之最,最大,辣椒', 'upload/20151125/text-3-12-1.jpg', null, '3', '世界之最', '2', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-25 16:07:27', '2015-11-25 16:07:28');
INSERT INTO `Article` VALUES ('13', '世界最大的復活节彩蛋', '世界最大的復活节彩蛋高达8米，这个巨型的復活节彩蛋是由专业的面点师制作的，此项纪录已被正式收入吉尼斯世界纪录大全，正式成为了世界上最大的復活节彩蛋。下图是在阿根廷的内格罗河省，安第斯山脚下的圣卡洛斯-德巴里洛切，众多的厨师们将这枚高达8米的巨型復活节彩蛋拆分后给观众们品尝。', '世界最大的復活节彩蛋高达8米，这个巨型的復活节彩蛋是由专业的面点师制作的，此项纪录已被正式收入吉尼斯世界纪录大全，正式成为了世界上最大的復活节彩蛋。下图是在阿根廷的内格罗河省，安第斯山脚下的圣卡洛斯-德巴里洛切，众多的厨师们将这枚高达8米的巨型復活节彩蛋拆分后给观众们品尝。', '最大,彩蛋', 'upload/20151126/logo-17-13-1.jpg', null, '17', '度哥世界之最', '3', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-26 21:44:03', '2015-11-26 21:45:43');
INSERT INTO `Article` VALUES ('14', '世界最长的婚紗', '每个女人都渴望穿上婚紗的那一刻，下面这位美女无疑是幸福的，因为她穿上了史上世界最长的婚紗。那你知道这件世界最长的婚紗出现在哪里，是那个幸运的新娘穿着，并且到底有多长吗？下面就跟着小编一起去看看吧。', '每个女人都渴望穿上婚紗的那一刻，下面这位美女无疑是幸福的，因为她穿上了史上世界最长的婚紗。那你知道这件世界最长的婚紗出现在哪里，是那个幸运的新娘穿着，并且到底有多长吗？下面就跟着小编一起去看看吧。', '最长,婚纱', 'upload/20151126/logo-17-14-1.jpg', null, '17', '强国论坛', '1', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-26 21:48:51', '2015-11-26 21:53:58');
INSERT INTO `Article` VALUES ('15', '世界最老的瑜珈教练', 'Tao Porchon-Lynch每天早上5点都会教她的学生们如何凹身体，拉筋等复杂的瑜伽动作，虽然她的年纪已近百岁。今年96岁的Tao从小在印度长大，目前居住在纽约的维彻斯特，她表示自己已经练了70多年的瑜伽了，而且没有停下来的准备。事实上，她还喜欢跳交际舞，而她的舞伴、今年26岁的Vard Margaryn整整小了她70岁。', 'Tao Porchon-Lynch每天早上5点都会教她的学生们如何凹身体，拉筋等复杂的瑜伽动作，虽然她的年纪已近百岁。今年96岁的Tao从小在印度长大，目前居住在纽约的维彻斯特，她表示自己已经练了70多年的瑜伽了，而且没有停下来的准备。事实上，她还喜欢跳交际舞，而她的舞伴、今年26岁的Vard Margaryn整整小了她70岁。', '最老,瑜珈教练', 'upload/20151126/logo-15-15-1.jpg', null, '15', '煎蛋', '13', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-26 22:04:01', '2015-11-26 22:11:11');
INSERT INTO `Article` VALUES ('16', '世界上臀部最大的女子', '世界上臀部最大的女人是美国人邁克爾斯·魯菲內利 （Mikel Ruffinelli），邁克爾斯·魯菲內利生活在美国洛杉磯，是世界上臀部最大的女人，她是4个孩子的妈妈，她的臀圍达到了8英尺长，大约合2.4米长。', '世界上臀部最大的女人是美国人邁克爾斯·魯菲內利 （Mikel Ruffinelli），邁克爾斯·魯菲內利生活在美国洛杉磯，是世界上臀部最大的女人，她是4个孩子的妈妈，她的臀圍达到了8英尺长，大约合2.4米长。', '最大,臀部,女人', 'upload/20151126/logo-15-16-1.jpg', null, '15', '度哥世界之最', '2', '0', '青菜萝卜', '1', '', '', '', '', '', '2015-11-26 22:31:35', '2015-11-26 22:31:37');

-- ----------------------------
-- Table structure for `Category`
