-- ----------------------------
-- Table structure for `Chapter`
-- ----------------------------
DROP TABLE IF EXISTS `Chapter`;
CREATE TABLE `Chapter` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `content` text COMMENT '章节内容',
  `articleId` bigint(20) DEFAULT NULL COMMENT '归属文章ID',
  `sort` int(11) DEFAULT '1' COMMENT '排序',
  `isDelete` bit(1) DEFAULT b'0' COMMENT '是否删除',
  `createTime` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `modifyTime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=31 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of Chapter
-- ----------------------------
INSERT INTO `Chapter` VALUES ('1', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这只座头鲸可能是米伽罗，它被认为是全世界唯一一只白色座头鲸。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">米伽罗被认为是为了热带的昆士兰北部温暖海域而从南极迁徙到澳大利亚的东部海岸。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-22-1-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据悉，这只饱受人们喜爱的座头鲸自1991年首度被海洋生物学家Dan Burns发现后，每年都会返回一次，今年已经是“米伽罗”往返迁徙的第23个年头。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在博特尼湾鲸鱼工作站志愿者的提醒下，摄影爱好者有幸在现场捕捉到“米伽罗”与其他座头鲸嬉戏追逐的身影。</span>\r\n</p>\r\n<p style=\"text-align:center\">\r\n    <span style=\"font-family: 宋体, SimSun;\"><img src=\"[basePath]/upload/20151123/text-22-1-2.jpg\" style=\"width: 500px; height: 340px;\"/></span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">35岁的“米伽罗”因其通体呈罕见的白色而显得弥足珍贵，已经受到联邦法律的保护，以防止人们向它靠近。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">当前，世界上与“米伽罗”一样珍稀的另一条白化巨鲸身处挪威。通常，座头鲸的迁徙从南极启程，抵达北昆士兰后，再于9月返回南极，这个迁徙过程大约持续3个月。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-22-1-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '1', '1', '', '2015-11-23 16:55:25', '2015-11-25 09:33:16');
INSERT INTO `Chapter` VALUES ('2', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据英国媒体报道，最受澳大利亚人们喜爱的白化座头鲸“米伽罗”（Migaloo）最近一次现身是在去年新南威尔士的格林角海岸，这令当地民众惊喜不已。随后的几天，“米伽罗”又数次出现在悉尼的Cronulla海滩，让人大饱眼福。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-22-1-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">米伽罗（Migaloo）是澳洲土着语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下。2013年9月，摄影师在澳大利亚昆士兰北部的埃蒂海湾拍摄到了一只全身白色的座头鲸。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-22-1-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">白色雄性座头鲸。米伽罗（Migaloo）是澳洲土着语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-22-1-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据生物学家的最新研究发现，座头鲸用以交流的“歌声”中包含有人类语言要素。不过尽管研究者认为鲸在本质上尚未拥有自己的语言，但他们还是发现了座头鲸歌声与人类语言之间的相似之处。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-22-1-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '1', '2', '', '2015-11-23 17:16:35', '2015-11-25 09:33:40');
INSERT INTO `Chapter` VALUES ('3', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">104岁老人长黑发换新牙，相信你一定觉得不可思议。湖北随县城关104岁婆婆杨传玉身体健康，不但生活自理，还能帮家人做些家务事。让人惊奇的是，杨传玉100岁以后竟然长了新牙和黑头发，而且皮肤不松弛很有弹性，当地人称这位百岁婆婆返老还童。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">老寿星杨传玉今年104岁，出生于1912年10月16日。杨传玉一生共育有8个子女，但其中4人出生不久就先后夭折，只有4人活了下来。如今，存活下来的4名子女中，年龄最大的已经70岁。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">杨传玉平时最爱吃南瓜和蒸肉，每天作息很规律，心态平和，从没有吃过保健品。如今，杨传玉已经四世同堂，而且身体健康能干家务，街坊邻里都夸她有福气。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-22-2-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">杨传玉能自己洗漱、换衣服，而且在家人忙时能帮忙做一些家务事，每天都把自己和屋子收拾得干净利落。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">虽然已有104岁高龄，一脸皱纹的杨传玉却面色红润，皮肤也很有弹性，脸上没有一块老年斑。尽管眼睛因白内看不清，但耳朵很灵光，而且口齿清晰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">更神奇的是，杨传玉在50多时做过一次体检，被查出有严重心脏病，随时可能死亡。这么多年过去了，老人一次都没犯过病。100岁以后，杨传玉还奇迹般地长了新牙和黑头发。</span>\r\n</p>', '2', '1', '', '2015-11-23 22:44:41', '2015-11-25 09:34:11');
INSERT INTO `Chapter` VALUES ('4', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">洛神甄宓是中山无极(今河北省定元县)人，祖上出了一位名人，叫做甄邯，在西汉末年和王莽新朝时做过太保、大司马。甄宓一出生，就带有传奇色彩。\r\n据说，其母怀她时，每每睡觉，总梦见一仙人，手持玉如意，站在旁边守护着她。临产时，那仙人又入得房中，将一件玉衣盖在她身上。紧接着，甄宓就呱呱落地。\r\n在那个时代，这种虚无缥缈的征兆总是引得人们浮想联翩。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">自小端庄守礼 少时便有贤名</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据《魏书》记载，这位未来的皇后，从小就喜静恶动，听话得很，从不胡闹。八岁时，有次外面来了卖艺的，骑马耍着一套一套的把戏，相当热闹。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">四个姐姐听得喧哗，都忍不住推开楼上的窗户观看。唯有甄宓，依然一声不吭地坐在自己房中。家人问起，她平静地说：这难道是女孩子应该观看的么?马戏什么的，最讨厌了。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">时值东汉末年，群雄并起，天下纷乱。甄宓家所在的中山郡，也被刘虞、公孙瓒、袁绍之间的征战所波及。兵连祸结，又闹起了灾荒，大片田地颗粒无收。老百姓为了挣扎着活命，稍微有几个钱的，拿出祖辈积攒的金银珠宝，换一点救命粮食，没钱的就只有等死。甄家作为世家大族，家中囤有很多粮食。张夫人乘机发国难财，高价卖粮，收入许多钱财。财帛日益增多，举家老小都乐得合不拢嘴，十来岁的甄宓却越来越不安。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">尤其是看到路边成群的饿殍和奄奄一息的人们眼中贪婪渴求的目光，善良和聪明的甄宓再也忍不住了。她就跑去对母亲说：母亲啊，我听说过一句话，匹夫无罪，怀璧有罪。现在世道这么乱，周围的乡邻都饥饿困乏，如果我们再这样靠囤粮求富，一定会遭祸的。不如把粮食拿出来分给乡邻吧!这样不仅为他人做了好事，对我们自身也是一种保护。张夫人听了，觉得有理，便开仓赈济饥民。甄家在当地素有号召力，附近的富户也效法开仓。灾荒得到缓解，地方也稳定下来。从此，洛神甄宓的贤名广为流传。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '3', '1', '', '2015-11-23 23:03:50', '2015-11-25 09:34:25');
INSERT INTO `Chapter` VALUES ('5', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">初为袁绍儿媳 后为曹操所掳而改嫁曹丕</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">名门世家之女，容貌美艳，贤淑有德，洛神甄宓几乎具备了那个时代对于女子的全部要求，堪称十全十美的尤物。建安初年(公元196年左右)，冀州牧袁绍派人来甄家，将甄宓聘给他的二儿子袁熙作为媳妇。但是甄宓与袁熙结婚后没多久，建安五年(公元200年)，袁绍在官渡之战中被曹操打败，两年后病死。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">随即，曹操引军进逼河北。袁绍的大儿子袁谭和三儿子袁尚为了争夺继承权而自相残杀，被曹操一一击破。袁谭和袁尚的妻子都奔逃到各自的丈夫那里，只有甄宓留在邺城，陪伴着无依无靠的婆婆。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">建安九年(公元204年)二月，曹操的大军包围了邺城。经过半年围城，城中箭尽粮绝，谋士审配的侄儿审荣开城投降，曹军进入邺城。破城之时，曹操立即派兵把守住绍府邸，不得伤害府中任何人，也不得让任何人进入。他则先去整理下仪容，准备拿出50岁男人最灼目的魅力，去征服那美丽而多才的甄宓。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">曹大人得意之时，他的儿子，未来的魏文帝曹丕，不顾军令，直闯袁绍府中。曹丕见到甄宓真正的容颜后，只觉头晕目眩，仿佛电流通过全身，连手中的剑都把持不住。等到曹操兴冲冲迈进门来，谁知却看到儿子已经先行闯入，顿时又尴尬又恼怒地愣在当场。曹丕跪在地上道：孩儿有违军令，贸然闯入，请父亲降罪。不过，孩儿对甄宓一见倾心，此生别无他求，只愿能有她在侧。孩儿一片真心，还望父亲成全。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">看甄宓一眼，曹操惊叹于她的美艳，却又立刻陷入矛盾与尴尬之中。最终他的理智战胜了情感，袁绍曾是自己的老朋友，把老朋友的儿媳妇据为己有，倒也罢了。可要是再传出去父子争一个女人，岂不被人耻笑?</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">权衡之下，曹操言不由衷地说了句“真是我的好儿媳啊”，“遂命曹丕纳之”。这一年，洛神甄宓23岁，她的第二任丈夫曹丕18岁。在嫁给曹丕的十多年里，她兢兢业业地完成着一个妻子的使命，为曹丕生下了儿子曹叡和女儿东乡公主。</span>\r\n</p>', '3', '2', '', '2015-11-23 23:10:19', '2015-11-25 09:34:39');
INSERT INTO `Chapter` VALUES ('6', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">红颜薄命 因一首《塘上行》为帝王所杀</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">自古帝王之家，总是祸福难常。甄宓名义上是曹丕的爱妻，实际却是战争中掳掠到的俘虏，是曹丕府邸中豢养的女奴。虽凭借美貌，暂时能得到曹丕的恩宠而保住性命，但美貌是不能长久的，恩宠更是不能长久的。因此，她必须谨慎迎合曹丕，以求保住自己的地位和生命。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">甄宓的生活，外表光鲜，而内心苦闷恐慌。在这时，又一个男子进入她的生活。他就是曹操的儿子，曹丕的弟弟曹植。这个才子，比曹丕小五岁，比甄宓小十岁，但他不知何时，竟也爱上了嫂子。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">建安二十五年(公元220年)正月，曹操病逝。同年十月，34岁的曹丕取汉献帝而代之，改元黄初，国号大魏，是为魏文帝。当上皇帝的曹丕，并没有封甄宓为皇后。因此，甄宓并未随着夫君的称帝而尊荣。相反，她一直郁郁寡欢。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-8.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">她做了一首《塘上行》，略抒情怀：“……众口铄黄金，使君生别离。念君去我时，独愁常苦悲。想见君颜色，感结伤心脾。念君常苦悲，夜夜不能寐……”表述了哀怨之意。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">却不料，这首诗给她带来了杀身之祸。当上皇帝的曹丕，脾气越发喜怒无常。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-3-9.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">他不但对曾与己争位的弟弟曹植屡加迫害，对于曾为曹魏政权立过大功的叔父曹洪，也因为一点旧怨就要加以杀害。看到甄宓这首诗后，曹丕认为她心怀怨念，竟然恼羞成怒，于黄初二年(公元221年)六月，将甄宓赐死。洛神甄宓时年40岁。</span>\r\n</p>', '3', '3', '', '2015-11-23 23:15:34', '2015-11-25 09:34:53');
INSERT INTO `Chapter` VALUES ('7', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">有明一代，为类避免前代的外戚之祸，妃子都选自民间，不重视门第，重视品行，妃子都出身于选秀女，不允许大臣推荐。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝早期，还实行严格的殉葬制度，皇帝死后妃子要殉葬，其惨烈程度历朝历代都很罕见，但即便如此，民间女性也不能回避选妃。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">实际上，即便明朝妃子选自民间，妃嫔也没有品级，但这一样能为家族带来益处，所以明代还是有不少人家愿意让女儿参与选妃。明朝选妃制度复杂，前后大致需要六个步骤，包括海选、初选、五官检查、裸检等步骤。以明熹宗朱由校的皇后张嫣为例，她便经历了漫长而复杂的选拔过程。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝选妃，首先在全国范围内海选姑娘，挑选5000名民间女孩，由官府发放路费，父母陪同前往京城备选，这一步便是海选。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">进入京城以后，凭借外貌身高由太监进行初选，其中长得矮的，过高的，太胖的或者太瘦的都会被淘汰，这一步会有1000人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">初选之后，会有太监进行更进一步的筛选。包括女孩的耳、眼、嘴等五官，腰围、皮肤、肩宽都属于帅选的细节，稍微不合适的就会被淘汰，哪怕皮肤上有颗黑痣也不能过关。并且这一步会考察姑娘们的声音，声音不好听或说话结巴，也会被淘汰，这一步会有2000人被淘汰。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前三步的筛选后，太监会拿工具测量剩下的人，看她们的身体是否符合比例，气质如何，长短手、脚大的、举止轻浮的人都会被淘汰，这一步会有1000人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前几步的严格筛选，现在就只剩下1000人，这1000人会经过严格的“裸检”。这一步由年老的宦官进行，姑娘们会被带到密室，摁乳房，查看长得挺不挺；闻闻腋下，有没有狐臭。最后摸遍全身，试试长得是否发达。这一步会有700人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前面的帅选，最后就是要长期观察，考察性情了，女孩们会被留在宫中生活一个月，考察她们的生活习性，说话态度、智商高低、人品优劣，这一步会有250人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前面这些严格的筛选，剩下的50人就可以当朱由校的妃子了。不过这不算完，还需要从中选出一人当皇后。这一步由先皇的宠妃昭妃（代行太后职责）面试，面试过关者就可以当皇后了。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">张嫣表现优异，外貌同样出众，于是最终被选为皇后。其过程可谓万里挑一，能在这种情况下脱颖而出，也是人才了。不过不知道这是幸运呢还是不行的开始呢？</span>\r\n</p>', '4', '1', '', '2015-11-23 23:45:04', '2015-11-25 09:35:14');
INSERT INTO `Chapter` VALUES ('8', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">一入宫廷深似海</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝宫女们一经选入宫内，便失去自由，大都是衣食菲薄，住所简陋，终身苦役，不能与父母相见。繁琐的礼节，森严的等级，不时的凌辱，几乎无出头之日。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">１、“提铃”和“板着”之罚。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">宫女们是被严格控制的。她们除了完成各种苦役之外，她们还要经常在知书女内官的教习下读《女训》、《女孝经》等书以扭曲她们的心灵。宫女稍有违规者，将被处以“墩锁”“提铃”和“板着”。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">“提铃”就是受罚宫女每夜自明宫德乾清宫门到日精门、月华门，然后回到乾清宫前。徐行正步，风雨不阻，高唱天下太平，声援而长，与铃声相应，这是一般的。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">“板着”就是受罚宫女面向北方立定，弯腰伸出双臂来，用手扳住两脚。不许身体弯曲，一直要持续一个时辰，即两个小时左右，一般情况是受罚宫女必定头晕目眩，僵仆卧地，甚有呕吐成疾，至殒命的。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">２、宫女生病无医，自生自灭。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝规定：“宫嫔以下有疾，医者不得入，以证取药。”宫嫔尚且如此，宫女自不待言。据《明宫史》载：</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在金鳌玉桥西、棂星门迤北羊房夹道（今名养蜂夹道），有内安乐堂，“有掌司总其事者二三十人。凡宫人病老或有罪，先发此处，待年久方再发外之浣衣局也。”</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这就是说，宫人得了病，或是年老了，要和有罪的人一样，发到这里，靠自己的生命力延续时日，或者等死。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">如果少数人偶被皇帝看中，地位略有改变，生得子女者尚能晋封，否则也只能幽闭深宫，了此一生。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">３、死无葬所，火烧后尸灰填入枯井。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝宫女一生锁闭深宫，供帝王玩弄、使役到死亡（包括各种死亡）后，如果不是有名的，都不会赐墓，而是火葬。火烧后将众多尸灰一起填入枯井。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">到了嘉靖年间，有一个贵嫔捐钱买了几亩民地，宫女不愿其尸灰入井的，则埋此地中。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据清人刘廷玑《在园杂志》卷三说：“墙固垒垒，碑亦林立，……每于风雨之夜，或现形，或作声，幽魂不散。”</span>\r\n</p>', '4', '2', '', '2015-11-23 23:45:22', '2015-11-25 09:35:29');
INSERT INTO `Chapter` VALUES ('9', '	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 黑体, SimHei;\">残忍变态的“性虐待”</span>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">在中国历史上，隋炀帝的荒淫无耻臭名昭著，然而明朝很多帝王更比隋炀帝厉害，他们的糜烂、淫乱更上一层楼。</span>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">据史记载，自宪宗至熹宗160多年间(1465～1627年)，皇帝和大臣见面的次数约略可数。宪宗在位23年，仅召见大学士万安等一次，只说了几句话就在万安高呼万岁之声中退朝了。</span>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">武宗在位16年，一次也没有召见大臣。世宗、神宗在位都达四五十年，都是20多年不视朝政，都忙于淫乐，其间不知道性病有多少宫女遭受过各种残酷的“性虐待”。</span>\r\n	</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"[basePath]/upload/20151123/text-27-4-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">明人笔记中记有神宗与宫女淫乐之事说：</span>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">夏日，明月高悬之夜，与后宫嬉。令人自轻罗制成之囊中，放出流萤无数。再令宫女以轻罗团扇争相扑捉，若流萤落于谁簪上，则是夜帝必幸之。故宫女争以香水洒于簪上，以待流萤。</span>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">秋日，帝题唐人王建宫词前二句于红叶上，令宫女题该词后二句于另一红叶，一起放入御沟，若遇两叶相叠，令人取观，如成全首宫词，则书后二句之宫女，是夜必获帝幸。</span>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">冬日，于洛阳殿大池，注满香汤，挑选柔肌雪肤之宫女，同浴于池，效鸳鸯戏水之乐。浴罢，则坐于锦绣上拥美饮酒，谓之“鸳鸯之会”。</span>\r\n	</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"[basePath]/upload/20151123/text-27-4-8.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">荒淫的明武宗让当时的大太监刘瑾设置豹房。豹房就是修建的特定建筑物，内设宫女和从外强来的民女等供帝王淫乐的场所。</span>\r\n	</p>\r\n	<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n		<span style=\"font-family: 宋体, SimSun;\">明武宗在内淫乐不够瘾，大肆对宫女“性虐待”，传说，还有强迫宫女与兽类交媾的事等，其“性虐待”之变态和残酷啊。</span>\r\n	</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"[basePath]/upload/20151123/text-27-4-9.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n	</p>', '4', '3', '', '2015-11-23 23:50:16', '2015-11-25 09:36:09');
INSERT INTO `Chapter` VALUES ('10', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">残酷而悲怆的“殉葬”</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝在中国历史上把变态残忍的殉葬之风推向又一个高潮，妃嫔殉葬成为明初封建统治的一种制度(到明英宗朱祁镇时被废止)，而且民间也深受影响。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明代皇帝死亡后，一些绝美的宫女，是被钦点“殉葬”的。据有关史料记载：朱元璋死以后有40个妃嫔殉葬，朱棣死时有30多妃嫔殉葬，明仁宗朱高炽(朱棣的儿子)执政不到一年，葬礼极为简略，但是也有 7个妃嫔殉葬，明宣宗朱瞻基有10个妃嫔殉葬等。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">《明史·后妃传》记载一个民间少女郭爱被选入宫中仅20天，朱瞻基死了，她得知自己被列入殉葬名单之后，含泪写下一首绝命词：“修短有数兮，不足较也;生而如梦兮，死则觉也;失吾亲而归兮，不足较也;惭余之不孝也，心凄凄而莫能已兮，则可悼也。”</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">《朝鲜李朝实录中的中国史料》记载明成祖朱棣死后明朝宫廷内逼宫人殉葬的情形：</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-10.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">等待殉葬的三十余名宫女吃完饭后，被引入一宫殿，凄惨的哭声震动宫殿。宫女们依次被送上木床，太监们将宫女的头颈按入丝带的套圈内，然后抽去木床，宫女在惨烈的挣扎中停止了更年期呼吸，一个个像伸着长长勃颈的雉鸡一样吊挂着。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">待宫女们死定后，仁宗(朱高炽)亲入“辞决”，“辞决”多么“谥美”的词藻啊。然后封这些怨寂的死尸为手术什么贞惠妃呀、贞丽妃呀、贞淑妃呀等等。这是多么的残忍和虚伪啊。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">反抗与“凌迟”杀戮</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">幽闭深宫的宫女长期堪忍受着明朝皇帝们变态和残忍的虐待(包括肌体、苦役、体罚、性虐待等)。据史书记载，仅嘉靖帝笞楚虐待致死的宫女多达200余人。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">嘉靖二十一年，即公元1542年十月，饱受虐待，濒于绝境的10几名宫女以柔弱素手干出了一件古今中外都不曾有过的大事——勒杀嘉靖帝，此大案历史上称作“壬寅宫变”。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">当时，由于宫女缺乏经验，误拴死结，没有把嘉靖帝勒死。后来，这10几名宫女都被凌迟处死。所谓“凌迟”，就是人们常说的“千刀万剐”的酷刑。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">宫女地位的低下，文献夫妻上是从来不记载她们的名字的，惟独此案有一个被剐宫女的详细名单。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151123/text-27-4-11.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">根据《明实录》记载：她们是杨金英、蓟川药、杨玉香、邢翠莲、姚淑翠、杨翠英、关梅秀、刘妙莲、陈菊花、王秀兰、张金莲、徐秋花、邓金香、张春景、黄玉莲。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这真是千古悲剧，万古绝杀啊!</span>\r\n</p>', '4', '4', '', '2015-11-23 23:54:24', '2015-11-25 09:36:26');
INSERT INTO `Chapter` VALUES ('11', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">最大的阴茎有多大呢？对于男性最隐私的部位，一般人肯定是不愿意透露的，本文将为大家4个拥有最大的阴茎的男人，分别是长达56厘米的非洲象人族，长达28.5厘米的俄罗斯传奇修道士拉斯普京，长达34厘米的美国男子Jonah Falcon和可以用自己阴茎让车轮转的中国秦朝的嫪毐，耐心看完，一个比一个精彩。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">1、非洲象人族，阴茎最长达56厘米</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">非洲象人族男人拥有世界上最大的阴茎，上面这位象人族男子，据说疲软状态下长度为33.5厘米，剖面直径为4.3厘米，（睾丸）约为鸡蛋大小。兴奋状态下长度达到56厘米，剖面直径为7.5厘米，睾丸约为鸭蛋大小，射精时精液能喷出6米以外，精液量平均值为95毫升（喝水的纸杯半杯）...</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-5-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-5-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">2、俄罗斯最大的阴茎，长达28.5厘米</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">世界最长阴径有多长？来看看俄罗斯最具传奇色彩的修道士拉斯普京，号称拥有世界上最大的阴茎，其自称阴茎长度为28.5cm，拉斯普京在俄国罗马诺夫沙皇时期，以其神秘性能力和性生活放荡而著称，他曾与无数女子发生过性行为，他的超常能力甚至赢得了皇后亚历山德拉的好感，拉斯普京曾与皇后整日狂欢作乐、随心所欲。后被杀死，阳具被割下用防腐剂泡着，放在博物馆，现在还在...</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-5-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '5', '1', '', '2015-11-24 22:31:00', '2015-11-25 09:36:43');
INSERT INTO `Chapter` VALUES ('12', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">3、美国男子Jonah Falcon，34厘米</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">1970年出生于美国纽约布鲁克林的Jonah Falcon，看似平淡无奇的人生，却因为他与众不同的身体器官，而完全改变。2006年初，Jonah Falcon在英国第四频道的纪录片中，以《世界上最大的阴茎》为名报道他的巨大阴茎而爆红全球。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-5-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">Falcon的阴茎长34.3厘米，是世上少有的真正拥有吹嘘资本的男人。他的私处第一次被曝光是在1999年，当时让节目制作人都目瞪口呆。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-5-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在福尔肯18岁的时候，当他的阴茎达到巨大到30.48厘米时，他就知道自己很特殊了。1999年HBO还播放了描写他的纪录片众多色情公司都有联系过他(但他从未接受)，并且国内几乎每个脱口秀节目都描述过他。</span>\r\n</p>\r\n\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">4、中国男子最大的阴茎，秦朝的嫪毐</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">有史记载的中国男子最大的阴茎拥有者是中国秦朝的嫪毐，《史记·吕不韦列传》记载他如下“始皇帝益壮，太后淫不止。吕不韦恐觉祸及己，乃私求大阴人嫪毐以为舍人，时纵倡乐，使毐以其阴关桐轮而行，令太后闻之，以啗太后。太后闻，果欲私得之。”翻译后就是嫪毐的那家伙因为大，被吕不韦当为门人养着，他居然能使生殖器插在车轮子中间的孔里，作为车轴，让车轮载着自己走。太后知道后，想单独占有这个人。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-5-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">最后想说的是，不要以为看过几部国外小电影，就以为那些黑人或白种人的那玩意都很大，实际上那都是极为少数的，其实世界各地人类大小都不会相差甚大，你就别惭愧了。</span>\r\n</p>', '5', '2', '', '2015-11-24 22:35:54', '2015-11-25 09:37:00');
INSERT INTO `Chapter` VALUES ('13', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">历史上寿命最长的皇帝是哪个皇帝呢？很多人认为是89岁的乾隆皇帝，严格意义上说是没错的，但如果范围放广一点，就不是乾隆帝了，寿命最长的皇帝应该是活了103岁的南越武帝赵佗，而寿命超过80岁的皇帝有5位，他们分别是萧衍、武则天、赵构、忽必烈、乾隆。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">1、南越武帝赵佗，寿命103岁，在位81年</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">赵佗（约公元前240年－公元前137年），今中国河北正定县人，原为秦朝将领，与任嚣南下攻打百越。秦末大乱时，赵佗割据岭南，建立南越国，赵佗活了近103岁，可以说是历史上寿命最长的皇帝。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-6-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">赵佗是南越国第一代王和皇帝，公元前203年至前137年在位，从前219年作为秦始皇平定南越的50万大军的副帅，一直到汉武帝刘彻建元四年（公元前137年）去世，一共治理岭南81年。其间由于他一直实行“和辑百越”的政策，促进了汉越民族的融合，并把中原地区的先进文化带到了南越之地，使南越得到了更好的发展。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">2、梁武帝萧衍，寿命86岁，在位48年</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">梁武帝萧衍(464年-549年)，字叔达，小字练儿。南兰陵中都里人(今江苏常州市武进区西北)。出生在秣陵(今南京)，为汉朝相国萧何的25世孙。南梁政权的建立者。萧衍在位时间达48年，在位颇有政绩，晚年爆发“侯景之乱”，都城陷落，被侯景囚禁，饿死于台城，享年86岁，葬于修陵，谥为武帝，庙号高祖，是中国历史上寿命最长的皇帝之一。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-6-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">3、乾隆帝，寿命89岁，在位60年</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">清高宗爱新觉罗弘历（1711年9月25日－1799年2月7日），清朝第六位皇帝，入关之后的第四位皇帝，25岁登基，在位60年，禅位后又任3年零4个月太上皇，实际行使国家最高权力长达63年零4个月，是中国历史上实际执掌国家最高权力时间最长的皇帝，也是中国历史上寿命最长的皇帝。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-6-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">4、武周女皇武则天，寿命81岁，在位15年</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">武则天(624年-705年)，汉族。中国历史上唯一一个正统的女皇帝，也是继位年龄最大的皇帝(67岁即位)，又是寿命最长的皇帝之一(终年82岁)。载初元年(公元690年)，武则天废睿宗，正式称帝，自号“圣神皇帝”，改国号为周，定都洛阳，史称“武周”或“南周”，公元705年退位。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-6-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '6', '1', '', '2015-11-24 22:50:08', '2015-11-25 09:37:17');
INSERT INTO `Chapter` VALUES ('14', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">5、宋高宗赵构，寿命61岁，在位35年</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">宋高宗赵构(公元1107年-1187年)，字德基，汉族。北宋灭亡后，赵构在南京应天府(今河南省商丘县南)即位，改年号为“建炎”。从建炎元年(1127年)到绍兴八年(1138年)的十余年间，高宗一直辗转在东南沿海各地，躲避金军。赵构在位36年，让位后病死，终年81岁。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-6-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">6、元世祖忽必烈，寿命80岁，在位35年</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">元世祖忽必烈(1215年-1294年)，元朝第5位皇帝，1260年至1294年在位(在位35年)，享年80岁，庙号世祖，谥号圣德神功文武皇帝，是中国历史上寿命最长的皇帝之一。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-6-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '6', '2', '', '2015-11-24 22:55:45', '2015-11-25 09:37:37');
INSERT INTO `Chapter` VALUES ('15', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">世界上最大的胸部是谁？现代社会的女人，腰越来越细，胸部越来越大，营养好是一个原因，更大的原因是可以人工隆胸了，世界上最大的胸部是一名来自美国的脱衣舞娘玛茜·蒙德斯，他的胸部重达36斤，已经获得吉尼斯记录认证，看一起看看有多大。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">世界上最大的胸部，双乳重达36斤</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">来自美国纽约长岛的玛茜·蒙德斯，生于1973年，之前是一名默默无闻的脱衣舞娘和色情女星，自从他给自己胸部加大到61MMM罩杯、重达18公斤后，一举世界闻名，这位拥有世界上最大的胸部的色情女星，在2000年隆胸后，结果隆出一对型号为61MMM的“超级波霸”。如今，身高1.83米的玛茜胸围竟为154厘米，每个乳房重达9公斤！</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">申请吉尼斯被拒</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">2003年，玛茜曾联系吉尼斯组织，咨询他们是否设有一个关于人造乳房的项目。可是对方当时回答称没有，只有“世界上最大的天然乳房”的记录，当时记录的保持着是中国的徐浩嘉。直至2005年，吉尼斯组织专门为玛茜设立了一个项目，她才正式被确认赢得“世界上最大的义乳”的世界纪录。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">玛茜身材天生婀娜匀称，原本胸部就已经很大，但为了让自己看上去更加性感火辣，她毅然决定隆胸。玛茜回忆道：“一开始，我往乳房注射的是老式的硅胶，可是后来风传这种填充物的各种恐怖故事，于是我让医生取出它们，改以生理盐水取而代之。”可是术后不久，玛茜的双乳便出现下垂，她不得不让医生在其腋窝下植入几根管子，以便让乳房支撑起来。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">玛茜身材天生婀娜匀称，原本胸部就已经很大，但为了让自己看上去更加性感火辣，她毅然决定隆胸。玛茜回忆道：“一开始，我往乳房注射的是老式的硅胶，可是后来风传这种填充物的各种恐怖故事，于是我让医生取出它们，改以生理盐水取而代之。”可是术后不久，玛茜的双乳便出现下垂，她不得不让医生在其腋窝下植入几根管子，以便让乳房支撑起来。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">最大的胸部却吓走男友</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">虽然拥有世界上最大的胸部，除了让她世界知名和能接到更多的成人电影外，也给她的生活带了困恼。变身“超级波霸”的她如今早已失去了男朋友。她说：“没有人敢结交我这样的女人，因为每当我出现在公共场合的时候，都会成为人们注视的焦点。我经常遇上一些令人啼笑皆非的事情。比如，大街上总有小伙子死盯着我看，结果要么迎头撞上障碍物，要么遭到身边女朋友的一记耳光。”</span>\r\n</p>\r\n</p>', '7', '1', '', '2015-11-24 23:18:40', '2015-11-25 09:37:54');
INSERT INTO `Chapter` VALUES ('16', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">好吧！如果你觉得玛茜的胸不够大，那么下面再给大家推荐几个更猛的！</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-7-8.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这些超级大胸妹纸，都是来自网络，没有真实信息可查，很多都是PS的，仅供大家点阅，你懂的！</span>\r\n</p>', '7', '2', '', '2015-11-24 23:23:46', '2015-11-25 09:38:12');
INSERT INTO `Chapter` VALUES ('17', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">正常情况下，婴儿应该是怀胎十月，可是世界上最小的婴儿，只在妈妈肚子里待了6个月；这位来自山东菏泽的妈妈，生了5胞胎，最小的女儿不幸夭折，剩下四个婴儿中，最小的婴儿仅仅250克，打破了此前由美国婴儿阿米利娅·泰勒保持的280克的世界上最小的婴儿记录。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">1、世界上最小的婴儿，仅仅250克</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-8-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">2015年1月9日晚9时，山东菏泽东明县31岁产妇吴俊平在菏泽市立医院产下五胞胎，4个女儿，1个儿子，让不少家庭羡慕不已。然而，由于早产，吴俊平生产的5个孩子，最小的一个女婴不幸夭折，其他4个孩子虽然全部存活，但仍住在新生儿重症监护室。由于早产，存活的四个婴儿中最大的只有1.135公斤，最小的一个男婴体重仅仅250克，这个数字甚至比此前世界上最小的婴儿，阿米利娅·泰勒还小，出生于美国迈阿密的阿米利娅·泰勒，在在其母亲肚子里待了21周零6天，出生时的体重只有280克，身长24厘米。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-8-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">2014年9月份，吴俊平去医院检查得知，自己怀上了五胞胎，这对于一个普通的家庭来说，是一件幸福的事，同时将来也会面临巨大的压力。1月9日晚8时许，张永立正吃着饭，妻子吴俊平突然要生产，9时许吴俊平产下5个婴儿，4个女儿和1个儿子，但最小的一个女儿不幸夭折，其余4个婴儿全部存活。“孩子一出生就被送到了新生儿重症监护室，从凌晨到第二天，最小的儿子已经抢救两次，还没脱离危险。”张永立告诉记者，孩子出生后，他只见过孩子一面。现在四个孩子都还在医院的新生儿重症监护病房。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-8-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '8', '1', '', '2015-11-24 23:57:58', '2015-11-25 09:38:28');
INSERT INTO `Chapter` VALUES ('18', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">2、阿米利娅，存活下来的世界上最小的婴儿</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-8-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">阿米利娅·泰勒，出生于美国迈阿密，为存活下来的世界最早产婴儿，在其母亲怀孕21周零6天时通过破腹产出生，出生时的体重只有280克，身长24厘米，仅仅比一支普通圆珠笔长。4个月后，负责节省的医院宣布，这名母亲怀孕不到6个月，就生下280克的世界上最小的婴儿，在加护病房接受照料4个月后，计划近日被带回家中，开始正常生活。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-8-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">此前所有怀孕23周以内就早产的婴儿中，没有一个顺利存活下来。“很难想象，她能够走得这么远，现在她正在变得像个真正的婴儿，阿米利娅能活下来，简直就是一个奇迹”母亲索尼娅·泰勒说，“虽然她现在只有1.8公斤，但对我来说她已经很胖了。”截止到2016年，这位曾经的世界上最小的婴儿已经10岁了，阿米利娅·泰勒依然健康的成长着，这得以与现在医学的高速发展。因为阿米利娅的母亲不想让外界对他们过多的打扰，所以现在10岁的阿米利娅并没有照片外流出来。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151124/text-4-8-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '8', '2', '', '2015-11-24 23:58:17', '2015-11-25 09:38:43');
INSERT INTO `Chapter` VALUES ('19', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">相信每一个人都喜欢花吧，而且都会追求世界上最美的花，但是世界上花的种类非常多，那些才是世界上最美丽的花被？国外一个花卉组织目前评选出了世界十大最美的花，跟着小编一起瞻仰，看是否有你喜欢的。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">1、世界上最美的花，昙花</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">昙花。世人总不选择她来代表爱情,害怕昙花只一现。因为昙花的传奇故事很美，而且它本身也美，所以，世上最美的花应该是昙花。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">因为月下美人是最专情于挚爱的花，所以才最美。昙花总是选在黎明时分朝露初凝的那一刻才绽放，所以有着月下美人之称。传说昙花是一个花神，她每天都开花，四季都很灿烂，她爱上了一个每天为她锄草的小伙子，后来玉帝知道了这件事情，就大发雷霆，要拆散鸳鸯。玉帝把花神贬为一生只能开一瞬间的花，不让她再和情郎相见，还把那个小伙子送去灵柩山出家，赐名韦驮，让他忘记前尘，忘记花神。可是花神却忘不了那个年轻的小伙子，她知道每年暮春时分，韦驼尊者都会上山采春露，为佛祖煎茶，就选在那个时候开花！希望能见韦驮尊者一面，就一次，一次就够了！遗憾的是，春去春来，花开花谢，韦驮还是不认得她！昙花一现只为韦驮 。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">2、天堂鸟，大自然惊艳之作</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">令人惊艳的花形像极了天堂鸟，正是这一点让它很特别。颜色美丽的天堂鸟花非常高：天堂鸟花的植株有1.5米高，花朵本身也很大。或许你只能在南非看到天堂鸟花，访问一下你本地的花店你就会发现：天堂鸟花不但是世界上最美丽的花之一，也是最受欢迎的花之一。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">3、玫瑰，爱情精灵</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">玫瑰是世界上最有名的花。几个世纪以来，无论任何情况、任何场合之下，花朵饱满、颜色鲜艳的玫瑰花一直深受各个不同种族人们的偏爱。根据花色的不同，它们有很多的象征意义。玫瑰或许对你来说并不奇特或新鲜，但它们绝对属于世上最美的花之一，这也恰恰是为什么我们都喜爱它的原因。</span>\r\n</p>', '9', '1', '', '2015-11-25 15:10:05', '2015-11-25 15:15:21');
INSERT INTO `Chapter` VALUES ('20', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">4、兰花，珍贵之花</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">兰花也以因其品种繁多而赫赫有名。除了最冷的南极洲外，它们适宜在任何大洲种植。不可思议的兰花，古老如岁月，精美如雨滴。然而几个世纪以前，兰花还是稀有珍贵之物，如今在许多家庭和办公室里都可以见到。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">5、马蹄莲，典雅高贵</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">马蹄莲花集朴素、典雅于一身。它的风采也被希腊人称颂：他们把它命名为“kallos”，意思是“美人”。虽然马蹄莲花一种极好的盆栽花，但是你要留心你的小孩和宠物，马蹄莲毒性很强，不能吃。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">6、风信子，美丽物语</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">风信子是春天里的花朵，装饰着世界上许许多多的花园。不过，多亏花卉学家们的努力，培育出了许多美妙的风信子，所以今天，我们能够在一年的任何时间里看到它们。它们是非常特别的花，非常敏感。一旦被剪断了，它们的美丽和芳香会迅速消失。它们也适合盆栽，所以你可以在同时在室内和花园里欣赏到它们。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">7、牡丹，富贵的象征</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">花园里花朵大大的、富丽堂皇的牡丹花看上去美极了，这就是为什么我们总能在户外布景看到它们。这些漂亮的花朵数百年来都被花卉专家和普通人们赞赏称颂。中国的帝王惊艳于它浓郁的花香和外观，把它命为国花。</span>\r\n</p>', '9', '2', '', '2015-11-25 15:13:19', '2015-11-25 15:15:41');
INSERT INTO `Chapter` VALUES ('21', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">8、睡莲，诗情画意</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-8.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">提及世界上最有名、最美丽的花之一，睡莲可谓举世皆知。这不光是因为它品种繁多、分布广泛，还因为它花形浪漫。在诗人的诗歌里，它是爱情的象征。著名的 艺术 家绘画它，众多的摄影家们拍摄它。它们是在大自然母亲的杰作之一。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">9、樱花，和平使者</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-9.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">樱花是日本最为珍贵的花，看上去也极为美丽。没有什么能比花团锦簇的樱花树更富有诗意了。对于热爱美丽和平的景象、感性的日本来说，喜爱樱花胜过一切花朵，这并没什么好惊讶的。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">10、非洲菊，炫丽夺目</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-9-10.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">非洲菊是世界上最美丽的花之一。它奇异的花样图案和强烈的色彩，让我把它选在了10种最美的花之中。在它的原产地南非，也被称为“富贵花”，可能是因为那让人称奇的、强烈的、有时甚至几种颜色组合在一起的炫丽夺目的色彩。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">第一眼相见，它们的花香、花容就让我们为之惊叹。我们用它们来点缀我们的头发、装点我们的家、修饰我们的公园，花朵制成的精油也让我们香气袭人。要是一个世界没有了花，那将会多么死气沉沉、黯淡无光。</span>\r\n</p>', '9', '3', '', '2015-11-25 15:15:54', '2015-11-25 15:17:19');
INSERT INTO `Chapter` VALUES ('22', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">不要被电影中仙境里的超大花当真了，现实世界里是没有那么大的花儿的，那么世界上最大的花是什么花？那就是鼎鼎有名的大王花，也是传说中的食人花，其花瓣直径可以达到1.4米，花骨朵中间甚至可以装下一个人，更奇怪的是大王花发散的是臭味。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">世界上最大的花，大王花(可以藏1个人)</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-10-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">可能很多人没听过大王花，不过如果说食人花，肯定很多让都听过，其实大王花就是传说中的食人花，它世界上最大的花朵。生长于马来半岛及婆罗洲、苏门答腊等岛屿。大花草无根无茎，是一种寄生 植物 ，靠吸收葡萄科植物的养分为生。根据吉尼斯世界记录的记载，大王花最大直径可达1.4米之大，花瓣厚约1.4厘米，重达10千克，花中心可装10多斤水，甚至藏1个人。大花草开花时奇臭无比，发出腐肉味的臭气，靠吸引厕蝇与 甲虫 为其传粉。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-10-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">大王花为什么会有臭味？</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">主要是为了延续下一代，大王花刚开花的时候还有一点香味，可很快就散发出一股很浓的臭味，这样的味道既能避免被大型的食草 动物 吃到，又能吸引一些喜爱臭味的小飞虫纷纷飞来，在采食花蜜的同时，也帮助了大王花传播花粉。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-10-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">大王花的花期只有4天（ 花期最长的花 ），凋谢后的大王花变成一摊黏乎乎的黑东西，而花柄处会结出一个腐烂的果实，果实里就藏着大王花的种子。大王花的种子很小，带黏性，当大象或其他动物踩上它时，就会带到别的地方生根发芽。15个月后，在森林的某处就会散发出腐烂的臭味，一朵新的大王花开花啦！</span>\r\n</p>', '10', '1', '', '2015-11-25 15:40:41', '2015-11-25 15:43:43');
INSERT INTO `Chapter` VALUES ('23', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">只听说过 蛇吃人 的事情，但还从来没听过 植物 吃人的，虽然世界上有食人花，不过食人花吃人大多的都是传说，并没有真凭实据的发生过，而今天要介绍的就是世界上最阴险的一种吃人植物，日轮花，它可以帮助它的党羽蜘蛛把人咬死，来看看是咋回事。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">世界上最阴险的吃人植物，日轮花</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">大家都知道，一听到亚马逊流域和亚马逊原始森林，总是跟人感觉怪怪的，因为这个地区有世界上种类最多的奇形异兽，还有些恐怖的植物，而日轮花就是生长在这里，它是一种吃人植物。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-11-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">与一般植物不同的是，这种花反应非常灵敏，而且力量很大。如果有人想摘它的花，无论碰到它的茎、叶或者花瓣，那细长的叶立即像鹰爪一样伸卷过来，把人紧紧抓住。同时，躲藏在日轮花旁边的大型蜘蛛——黑寡妇蛛（这种蜘蛛有奇毒，在十大 世界上最毒的蜘蛛 排行榜中排名第4），便迅速赶来咬食 人体 。这种蜘蛛的上鄂内有毒腺，能分泌出一种神经性毒蛋白液体，当毒液进入人体，就会在极短的时间致人死亡。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">那么蜘蛛为啥要帮日轮花呢？原来蜘蛛吃饱消化排出的粪便，就成为日轮花的肥料，因此日轮花与蜘蛛之间存在着互利共生。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">日轮花与黑寡妇蜘蛛狼狈为奸</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-11-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">日轮花为什么要为蜘蛛猎取食物呢？原来尸体成了黑蜘蛛的食粮，黑蜘蛛吃了人的身体之后，所排出的粪便是日轮花的一种特别养料。凡有日轮花的地方，必定有吃人的大蜘蛛，它们互相利用，彼此依赖以共存。因此，日轮花就潜心尽力地为黑蜘蛛捕猎食物，它们互利共生，凡是有日轮花的地方，必有吃人的黑寡妇蜘蛛。当地的南美洲人，对日轮花十分恐惧，每当看到它就要远远避开。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">地球上到底是否存在吃人植物</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-11-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">有些学者们认为，在已发现的食肉植物中，捕食的对象仅仅是小小的昆虫而已，它们分泌出的消化液，对小虫子来说就是汪洋大海，但对人或较大的 动物 来说，简直微不足道。因此，很难使人相信地球上存在吃人植物的说法。但也有一些学者认为，虽然眼下还没有足够证据说明吃人植物的存在，但是也不应该武断地加以彻底否定。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">因为这种植物非常罕见，到目前并没有想过图片，而上面所配图片都是西番莲的花，也就是平时榨果汁用的百香果。</span>\r\n</p>', '11', '1', '', '2015-11-25 15:57:07', '2015-11-25 15:58:01');
INSERT INTO `Chapter` VALUES ('24', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">世界上最大的 辣椒 ，长达34.4厘米</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">世界上最辣的辣椒 是英国多西特郡出产的一种叫“纳加”红椒。而美国北卡罗莱纳州一位名叫瑟福的农民，培育了一只长达34.4厘米的辣椒。可以称得上是世界上最大的辣椒，因为没有过多的图片外漏，所以下面一起来看看那些关于辣椒的恐怖场景吧！</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-12-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-12-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-12-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151125/text-3-12-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '12', '1', '', '2015-11-25 16:07:55', '2015-11-25 16:08:19');
INSERT INTO `Chapter` VALUES ('25', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">世界最大的復活节彩蛋高达8米，这个巨型的復活节彩蛋是由专业的面点师制作的，此项纪录已被正式收入吉尼斯世界纪录大全，正式成为了世界上最大的復活节彩蛋。下图是在阿根廷的内格罗河省，安第斯山脚下的圣卡洛斯-德巴里洛切，众多的厨师们将这枚高达8米的巨型復活节彩蛋拆分后给观众们品尝。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-17-13-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '13', '1', '', '2015-11-26 21:43:31', '2015-11-26 21:43:39');
INSERT INTO `Chapter` VALUES ('26', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">2014年9月24日上午十点，记着来到位于成都天府新区太平镇双简大道侧的“紫颐·香薰山谷”。一款号称世界上最长的婚纱现身于此。据悉，这款婚纱集百人之力，耗时一个月，用单层网纱精心制作而成，其拖尾宽度1.5米，长约4100米，已经打破了2012年由罗马尼亚人创造的2977米的世界最长婚纱吉尼斯纪录。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-17-14-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在香薰山谷七彩花带，记着看到，世界最长婚纱已经放置其间。据工作人员介绍，世界最长婚纱今天只是预演，并未全部铺设，十一期间将正式与成都市民见面。趁着今天难得的好天气，已经有几对新人在这世界最长婚纱的见证下，记录下了他们幸福美好的时刻。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-17-14-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">除了能观看到世界最长婚纱，十一期间，正是园区四季薰衣草和马鞭草的盛花期，此外还有千日紫、醉蝶花等众多花卉景观。国庆期间，这里还将举办丰富多彩的活动。大假不愿出远门的朋友们，可以来这里徜徉花海记录美的瞬间。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-17-14-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-17-14-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', '14', '1', '', '2015-11-26 21:55:46', '2015-11-26 21:55:57');
INSERT INTO `Chapter` VALUES ('27', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">Tao Porchon-Lynch每天早上5点都会教她的学生们如何凹身体，拉筋等复杂的瑜伽动作，虽然她的年纪已近百岁。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">今年96岁的Tao从小在印度长大，目前居住在纽约的维彻斯特，她表示自己已经练了70多年的瑜伽了，而且没有停下来的准备。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">事实上，她还喜欢跳交际舞，而她的舞伴、今年26岁的Vard Margaryn整整小了她70岁。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">Tao奶奶从80年代开始就展露出舞蹈方面的天赋，到目前为止她已经斩获超过500多个比赛的第一名，而大部分获奖的原因都是来自其最喜欢的探戈。</span>\r\n</p>', '15', '1', '', '2015-11-26 22:09:22', '2015-11-26 22:09:30');
INSERT INTO `Chapter` VALUES ('28', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">“瑜伽是舞蹈的灵魂。”Tao在接受采访时曾说过，“而交际舞则会使身体感受到我们的快乐。”</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">Tao表示自己在8岁的时候就开始学习瑜伽，而成年后便决定要成为一名专业的瑜伽教练。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">Tao的爸爸是一个法国人而妈妈是印度人，她在年轻的时候曾是一个时尚模特。她还曾出演过1951年的电影《Show Boat》和1954年由Elizabeth Taylor主演的《The Last Time I Saw Paris》。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">Tao表示自己在8岁的时候就开始学习瑜伽，而成年后便决定要成为一名专业的瑜伽教练。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">现在虽然年逾90岁，这名精力充沛的瑜伽者表示瑜伽这项运动不仅使她的身体一直保持年轻的状态，还完善了她的思想和灵魂。</span>\r\n</p>', '15', '2', '', '2015-11-26 22:15:34', '2015-11-26 22:15:48');
INSERT INTO `Chapter` VALUES ('29', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">“瑜伽使我身心合一。”她解释道，“就像自然一样，一切都归于循环。我发现我在做瑜伽的时候身心能得到治愈，它带走了我的压力并帮我克服困难。“</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">其实虽然如此，时间还是带给了她一些不可避免的健康问题。几年前，Tao动了髋关节置换手术，而2011年的时候她还摔断过自己的手腕。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">不过这些伤痛都没能阻止Tao热爱瑜伽的决心，她仍然每天锻炼并感到自己每天都更加地年轻与强壮。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-8.jpg\" style=\"width: 500px; height: 600px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">另外，她能保持如此年轻强壮的秘诀之一是她有一点素食主义，并喜欢红酒和巧克力。身高为5英尺5英寸的Tao一直将体重维持在102磅。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">”我们都不该暴饮暴食。应该用心感受自然和生活，这会让我们更健康。“她说。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-9.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">除了开设瑜伽训练班外，Tao还曾在2011年出过一本关于冥想的书，并在2013年出过一套瑜伽教学的DVD。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">亚马逊上关于她的书有一段评价：”Tao是一个活生生的突破极限的例子。她用自己特殊的能力战胜了时间，并达到了瑜伽中所倡导的身心合一。Tao的人生哲学是：‘只有用到内心的力量，我们才能战胜各种困难。’“</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-15-10.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据悉，Tao还在1967年成立了美国红酒协会，并经常为各种红酒大赛担当评审。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">2012年，她正式被认定为世界最老的瑜伽教练。</span>\r\n</p>', '15', '3', '', '2015-11-26 22:19:15', '2015-11-26 22:20:45');
INSERT INTO `Chapter` VALUES ('30', '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">世界上臀部最大的女人是美国人邁克爾斯·魯菲內利 （Mikel Ruffinelli），邁克爾斯·魯菲內利生活在美国洛杉磯，是世界上臀部最大的女人，她是4个孩子的妈妈，她的臀圍达到了8英尺长，大约合2.4米长。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-16-1.jpg\" style=\"width: 400px; height: 550px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-16-2.jpg\" style=\"width: 400px; height: 550px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-16-3.jpg\" style=\"width: 400px; height: 550px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-16-4.jpg\" style=\"width: 400px; height: 550px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-16-5.jpg\" style=\"width: 400px; height: 550px;\"/>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"[basePath]/upload/20151126/text-15-16-6.jpg\" style=\"width: 400px; height: 550px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">魯菲內利身大大约1.6米，体重190公斤，每天她在吃旧3000卡路裡的食物，在她22岁时生下第一个儿子后，她的休重就开始增加，后来又怀孕三次，导致他的臀圍猛增到现在的2.4米长。但她对自己丰满的身型相当的满意。丰满的身材让她还在一个“大美女”的网站可赚取外块。</span>\r\n</p>', '16', '1', '', '2015-11-26 22:35:11', '2015-11-26 22:35:18');

-- ----------------------------
-- Table structure for `Comment`
-- ----------------------------
DROP TABLE IF EXISTS `Comment`;
CREATE TABLE `Comment` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `name` varchar(256) DEFAULT NULL COMMENT '称呼',
  `content` varchar(1024) DEFAULT NULL COMMENT '评论内容',
  `logo` varchar(256) DEFAULT NULL COMMENT '图标',
  `articleId` bigint(20) DEFAULT NULL,
  `isDelete` bit(1) DEFAULT b'0' COMMENT '是否删除',
  `createTime` timestamp NULL DEFAULT NULL COMMENT '创建时间',
  `modifyTime` timestamp NULL DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

