INSERT INTO `Article` (`id`, `title`, `abstract`, `content`, `tag`, `logo`, `logo2`, `categoryId`, `source`, `views`, `reviews`, `editor`, `listType`, `isHeadlines`, `isPlacedHome`, `isFeatured`, `isPublish`, `isDelete`, `createTime`, `modifyTime`) VALUES (1, '全世界唯一一只白色座头鲸', '米伽罗是世界上唯一的白色雄性座头鲸。米伽罗是澳洲土著语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下', '2011年10月，一头通体全白的座头鲸在澳大利亚大堡礁附近出现，出生数周，大概有12英尺(约3.65米)长。这种纯白色座头鲸估计在澳大利亚东海岸水域仅有10到15头，出现几率仅为千分之一。目击者韦恩·费文思看见了座头鲸浮出水面的瞬间。“这是一辈子也轮不上的运气。”费文思说，“我们当时正在漂流，突然，我看见一只白色的小鲸鱼出现在海面上。我当时简直不敢相信自己的眼睛。”', '世界唯一,座头鲸,动物,奇闻怪事', 'upload/20151123/logo-22-1-1.jpg', '', 22, '米尔网', 33, 0, '青菜萝卜', 1, '', '', '', '', '', '2015-11-23 16:42:36', '2015-11-23 23:02:02');
INSERT INTO `Article` (`id`, `title`, `abstract`, `content`, `tag`, `logo`, `logo2`, `categoryId`, `source`, `views`, `reviews`, `editor`, `listType`, `isHeadlines`, `isPlacedHome`, `isFeatured`, `isPublish`, `isDelete`, `createTime`, `modifyTime`) VALUES (2, '百岁婆婆返老还童', '104岁老人长黑发换新牙，相信你一定觉得不可思议。湖北随县城关104岁婆婆杨传玉身体健康，不但生活自理，还能帮家人做些家务事。让人惊奇的是，杨传玉100岁以后竟然长了新牙和黑头发，而且皮肤不松弛很有弹性，当地人称这位百岁婆婆返老还童。', '104岁老人长黑发换新牙，相信你一定觉得不可思议。湖北随县城关104岁婆婆杨传玉身体健康，不但生活自理，还能帮家人做些家务事。让人惊奇的是，杨传玉100岁以后竟然长了新牙和黑头发，而且皮肤不松弛很有弹性，当地人称这位百岁婆婆返老还童。更神奇的是，杨传玉在50多时做过一次体检，被查出有严重心脏病，随时可能死亡。这么多年过去了，老人一次都没犯过病。', '返老还童,奇闻怪事', 'upload/20151123/logo-22-2-1.jpg', NULL, 22, '中国网', 5, 0, '青菜萝卜', 1, '', '', '', '', '', '2015-11-23 22:46:01', '2015-11-23 22:47:11');
INSERT INTO `Article` (`id`, `title`, `abstract`, `content`, `tag`, `logo`, `logo2`, `categoryId`, `source`, `views`, `reviews`, `editor`, `listType`, `isHeadlines`, `isPlacedHome`, `isFeatured`, `isPublish`, `isDelete`, `createTime`, `modifyTime`) VALUES (3, '三国美女之洛神', '洛神甄宓是中山无极(今河北省定元县)人，祖上出了一位名人，叫做甄邯，在西汉末年和王莽新朝时做过太保、大司马。甄宓一出生，就带有传奇色彩。', '洛神甄宓是中山无极(今河北省定元县)人，祖上出了一位名人，叫做甄邯，在西汉末年和王莽新朝时做过太保、大司马。甄宓一出生，就带有传奇色彩。\r\n据说，其母怀她时，每每睡觉，总梦见一仙人，手持玉如意，站在旁边守护着她。临产时，那仙人又入得房中，将一件玉衣盖在她身上。紧接着，甄宓就呱呱落地。\r\n在那个时代，这种虚无缥缈的征兆总是引得人们浮想联翩。', '美女,历史趣闻', 'upload/20151123/logo-27-3-1.jpg', '', 27, '49363', 3, 0, '青菜萝卜', 1, '', '', '', '', '', '2015-11-23 23:03:08', '2015-11-23 23:03:10');
INSERT INTO `Article` (`id`, `title`, `abstract`, `content`, `tag`, `logo`, `logo2`, `categoryId`, `source`, `views`, `reviews`, `editor`, `listType`, `isHeadlines`, `isPlacedHome`, `isFeatured`, `isPublish`, `isDelete`, `createTime`, `modifyTime`) VALUES (4, '古代选妃女子竟要被太监摸遍', '有明一代，为类避免前代的外戚之祸，妃子都选自民间，不重视门第，重视品行，妃子都出身于选秀女，不允许大臣推荐。明朝早期，还实行严格的殉葬制度，皇帝死后妃子要殉葬，其惨烈程度历朝历代都很罕见，但即便如此，民间女性也不能回避选妃。', '有明一代，为类避免前代的外戚之祸，妃子都选自民间，不重视门第，重视品行，妃子都出身于选秀女，不允许大臣推荐。明朝早期，还实行严格的殉葬制度，皇帝死后妃子要殉葬，其惨烈程度历朝历代都很罕见，但即便如此，民间女性也不能回避选妃。', '历史趣闻', 'upload/20151123/logo-27-4-1.jpg', NULL, 27, NULL, 9, 0, NULL, 1, '', '', '', '', '', '2015-11-23 23:39:55', '2015-11-23 23:39:57');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (1, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这只座头鲸可能是米伽罗，它被认为是全世界唯一一只白色座头鲸。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">米伽罗被认为是为了热带的昆士兰北部温暖海域而从南极迁徙到澳大利亚的东部海岸。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-1-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据悉，这只饱受人们喜爱的座头鲸自1991年首度被海洋生物学家Dan Burns发现后，每年都会返回一次，今年已经是“米伽罗”往返迁徙的第23个年头。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在博特尼湾鲸鱼工作站志愿者的提醒下，摄影爱好者有幸在现场捕捉到“米伽罗”与其他座头鲸嬉戏追逐的身影。</span>\r\n</p>\r\n<p style=\"text-align:center\">\r\n    <span style=\"font-family: 宋体, SimSun;\"><img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-1-2.jpg\" style=\"width: 500px; height: 340px;\"/></span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">35岁的“米伽罗”因其通体呈罕见的白色而显得弥足珍贵，已经受到联邦法律的保护，以防止人们向它靠近。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">当前，世界上与“米伽罗”一样珍稀的另一条白化巨鲸身处挪威。通常，座头鲸的迁徙从南极启程，抵达北昆士兰后，再于9月返回南极，这个迁徙过程大约持续3个月。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-1-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', 1, 1, '', '2015-11-23 16:55:25', '2015-11-23 22:37:21');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (2, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据英国媒体报道，最受澳大利亚人们喜爱的白化座头鲸“米伽罗”（Migaloo）最近一次现身是在去年新南威尔士的格林角海岸，这令当地民众惊喜不已。随后的几天，“米伽罗”又数次出现在悉尼的Cronulla海滩，让人大饱眼福。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-1-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">米伽罗（Migaloo）是澳洲土着语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下。2013年9月，摄影师在澳大利亚昆士兰北部的埃蒂海湾拍摄到了一只全身白色的座头鲸。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-1-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">白色雄性座头鲸。米伽罗（Migaloo）是澳洲土着语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-1-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据生物学家的最新研究发现，座头鲸用以交流的“歌声”中包含有人类语言要素。不过尽管研究者认为鲸在本质上尚未拥有自己的语言，但他们还是发现了座头鲸歌声与人类语言之间的相似之处。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-1-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', 1, 2, '', '2015-11-23 17:16:35', '2015-11-23 23:15:26');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (3, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">104岁老人长黑发换新牙，相信你一定觉得不可思议。湖北随县城关104岁婆婆杨传玉身体健康，不但生活自理，还能帮家人做些家务事。让人惊奇的是，杨传玉100岁以后竟然长了新牙和黑头发，而且皮肤不松弛很有弹性，当地人称这位百岁婆婆返老还童。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">老寿星杨传玉今年104岁，出生于1912年10月16日。杨传玉一生共育有8个子女，但其中4人出生不久就先后夭折，只有4人活了下来。如今，存活下来的4名子女中，年龄最大的已经70岁。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">杨传玉平时最爱吃南瓜和蒸肉，每天作息很规律，心态平和，从没有吃过保健品。如今，杨传玉已经四世同堂，而且身体健康能干家务，街坊邻里都夸她有福气。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-22-2-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">杨传玉能自己洗漱、换衣服，而且在家人忙时能帮忙做一些家务事，每天都把自己和屋子收拾得干净利落。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">虽然已有104岁高龄，一脸皱纹的杨传玉却面色红润，皮肤也很有弹性，脸上没有一块老年斑。尽管眼睛因白内看不清，但耳朵很灵光，而且口齿清晰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">更神奇的是，杨传玉在50多时做过一次体检，被查出有严重心脏病，随时可能死亡。这么多年过去了，老人一次都没犯过病。100岁以后，杨传玉还奇迹般地长了新牙和黑头发。</span>\r\n</p>', 2, 1, '', '2015-11-23 22:44:41', '2015-11-23 22:44:43');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (4, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">洛神甄宓是中山无极(今河北省定元县)人，祖上出了一位名人，叫做甄邯，在西汉末年和王莽新朝时做过太保、大司马。甄宓一出生，就带有传奇色彩。\r\n据说，其母怀她时，每每睡觉，总梦见一仙人，手持玉如意，站在旁边守护着她。临产时，那仙人又入得房中，将一件玉衣盖在她身上。紧接着，甄宓就呱呱落地。\r\n在那个时代，这种虚无缥缈的征兆总是引得人们浮想联翩。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">自小端庄守礼 少时便有贤名</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据《魏书》记载，这位未来的皇后，从小就喜静恶动，听话得很，从不胡闹。八岁时，有次外面来了卖艺的，骑马耍着一套一套的把戏，相当热闹。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">四个姐姐听得喧哗，都忍不住推开楼上的窗户观看。唯有甄宓，依然一声不吭地坐在自己房中。家人问起，她平静地说：这难道是女孩子应该观看的么?马戏什么的，最讨厌了。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">时值东汉末年，群雄并起，天下纷乱。甄宓家所在的中山郡，也被刘虞、公孙瓒、袁绍之间的征战所波及。兵连祸结，又闹起了灾荒，大片田地颗粒无收。老百姓为了挣扎着活命，稍微有几个钱的，拿出祖辈积攒的金银珠宝，换一点救命粮食，没钱的就只有等死。甄家作为世家大族，家中囤有很多粮食。张夫人乘机发国难财，高价卖粮，收入许多钱财。财帛日益增多，举家老小都乐得合不拢嘴，十来岁的甄宓却越来越不安。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">尤其是看到路边成群的饿殍和奄奄一息的人们眼中贪婪渴求的目光，善良和聪明的甄宓再也忍不住了。她就跑去对母亲说：母亲啊，我听说过一句话，匹夫无罪，怀璧有罪。现在世道这么乱，周围的乡邻都饥饿困乏，如果我们再这样靠囤粮求富，一定会遭祸的。不如把粮食拿出来分给乡邻吧!这样不仅为他人做了好事，对我们自身也是一种保护。张夫人听了，觉得有理，便开仓赈济饥民。甄家在当地素有号召力，附近的富户也效法开仓。灾荒得到缓解，地方也稳定下来。从此，洛神甄宓的贤名广为流传。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', 3, 1, '', '2015-11-23 23:03:50', '2015-11-23 23:10:07');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (5, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">初为袁绍儿媳 后为曹操所掳而改嫁曹丕</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">名门世家之女，容貌美艳，贤淑有德，洛神甄宓几乎具备了那个时代对于女子的全部要求，堪称十全十美的尤物。建安初年(公元196年左右)，冀州牧袁绍派人来甄家，将甄宓聘给他的二儿子袁熙作为媳妇。但是甄宓与袁熙结婚后没多久，建安五年(公元200年)，袁绍在官渡之战中被曹操打败，两年后病死。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">随即，曹操引军进逼河北。袁绍的大儿子袁谭和三儿子袁尚为了争夺继承权而自相残杀，被曹操一一击破。袁谭和袁尚的妻子都奔逃到各自的丈夫那里，只有甄宓留在邺城，陪伴着无依无靠的婆婆。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">建安九年(公元204年)二月，曹操的大军包围了邺城。经过半年围城，城中箭尽粮绝，谋士审配的侄儿审荣开城投降，曹军进入邺城。破城之时，曹操立即派兵把守住绍府邸，不得伤害府中任何人，也不得让任何人进入。他则先去整理下仪容，准备拿出50岁男人最灼目的魅力，去征服那美丽而多才的甄宓。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">曹大人得意之时，他的儿子，未来的魏文帝曹丕，不顾军令，直闯袁绍府中。曹丕见到甄宓真正的容颜后，只觉头晕目眩，仿佛电流通过全身，连手中的剑都把持不住。等到曹操兴冲冲迈进门来，谁知却看到儿子已经先行闯入，顿时又尴尬又恼怒地愣在当场。曹丕跪在地上道：孩儿有违军令，贸然闯入，请父亲降罪。不过，孩儿对甄宓一见倾心，此生别无他求，只愿能有她在侧。孩儿一片真心，还望父亲成全。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">看甄宓一眼，曹操惊叹于她的美艳，却又立刻陷入矛盾与尴尬之中。最终他的理智战胜了情感，袁绍曾是自己的老朋友，把老朋友的儿媳妇据为己有，倒也罢了。可要是再传出去父子争一个女人，岂不被人耻笑?</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">权衡之下，曹操言不由衷地说了句“真是我的好儿媳啊”，“遂命曹丕纳之”。这一年，洛神甄宓23岁，她的第二任丈夫曹丕18岁。在嫁给曹丕的十多年里，她兢兢业业地完成着一个妻子的使命，为曹丕生下了儿子曹叡和女儿东乡公主。</span>\r\n</p>', 3, 2, '', '2015-11-23 23:10:19', '2015-11-23 23:15:17');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (6, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">红颜薄命 因一首《塘上行》为帝王所杀</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">自古帝王之家，总是祸福难常。甄宓名义上是曹丕的爱妻，实际却是战争中掳掠到的俘虏，是曹丕府邸中豢养的女奴。虽凭借美貌，暂时能得到曹丕的恩宠而保住性命，但美貌是不能长久的，恩宠更是不能长久的。因此，她必须谨慎迎合曹丕，以求保住自己的地位和生命。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">甄宓的生活，外表光鲜，而内心苦闷恐慌。在这时，又一个男子进入她的生活。他就是曹操的儿子，曹丕的弟弟曹植。这个才子，比曹丕小五岁，比甄宓小十岁，但他不知何时，竟也爱上了嫂子。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">建安二十五年(公元220年)正月，曹操病逝。同年十月，34岁的曹丕取汉献帝而代之，改元黄初，国号大魏，是为魏文帝。当上皇帝的曹丕，并没有封甄宓为皇后。因此，甄宓并未随着夫君的称帝而尊荣。相反，她一直郁郁寡欢。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-8.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">她做了一首《塘上行》，略抒情怀：“……众口铄黄金，使君生别离。念君去我时，独愁常苦悲。想见君颜色，感结伤心脾。念君常苦悲，夜夜不能寐……”表述了哀怨之意。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">却不料，这首诗给她带来了杀身之祸。当上皇帝的曹丕，脾气越发喜怒无常。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-3-9.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">他不但对曾与己争位的弟弟曹植屡加迫害，对于曾为曹魏政权立过大功的叔父曹洪，也因为一点旧怨就要加以杀害。看到甄宓这首诗后，曹丕认为她心怀怨念，竟然恼羞成怒，于黄初二年(公元221年)六月，将甄宓赐死。洛神甄宓时年40岁。</span>\r\n</p>', 3, 3, '', '2015-11-23 23:15:34', '2015-11-23 23:18:30');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (7, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">有明一代，为类避免前代的外戚之祸，妃子都选自民间，不重视门第，重视品行，妃子都出身于选秀女，不允许大臣推荐。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝早期，还实行严格的殉葬制度，皇帝死后妃子要殉葬，其惨烈程度历朝历代都很罕见，但即便如此，民间女性也不能回避选妃。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">实际上，即便明朝妃子选自民间，妃嫔也没有品级，但这一样能为家族带来益处，所以明代还是有不少人家愿意让女儿参与选妃。明朝选妃制度复杂，前后大致需要六个步骤，包括海选、初选、五官检查、裸检等步骤。以明熹宗朱由校的皇后张嫣为例，她便经历了漫长而复杂的选拔过程。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-2.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝选妃，首先在全国范围内海选姑娘，挑选5000名民间女孩，由官府发放路费，父母陪同前往京城备选，这一步便是海选。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">进入京城以后，凭借外貌身高由太监进行初选，其中长得矮的，过高的，太胖的或者太瘦的都会被淘汰，这一步会有1000人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">初选之后，会有太监进行更进一步的筛选。包括女孩的耳、眼、嘴等五官，腰围、皮肤、肩宽都属于帅选的细节，稍微不合适的就会被淘汰，哪怕皮肤上有颗黑痣也不能过关。并且这一步会考察姑娘们的声音，声音不好听或说话结巴，也会被淘汰，这一步会有2000人被淘汰。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前三步的筛选后，太监会拿工具测量剩下的人，看她们的身体是否符合比例，气质如何，长短手、脚大的、举止轻浮的人都会被淘汰，这一步会有1000人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前几步的严格筛选，现在就只剩下1000人，这1000人会经过严格的“裸检”。这一步由年老的宦官进行，姑娘们会被带到密室，摁乳房，查看长得挺不挺；闻闻腋下，有没有狐臭。最后摸遍全身，试试长得是否发达。这一步会有700人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前面的帅选，最后就是要长期观察，考察性情了，女孩们会被留在宫中生活一个月，考察她们的生活习性，说话态度、智商高低、人品优劣，这一步会有250人被淘汰。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">经过前面这些严格的筛选，剩下的50人就可以当朱由校的妃子了。不过这不算完，还需要从中选出一人当皇后。这一步由先皇的宠妃昭妃（代行太后职责）面试，面试过关者就可以当皇后了。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">张嫣表现优异，外貌同样出众，于是最终被选为皇后。其过程可谓万里挑一，能在这种情况下脱颖而出，也是人才了。不过不知道这是幸运呢还是不行的开始呢？</span>\r\n</p>', 4, 1, '', '2015-11-23 23:45:04', '2015-11-23 23:45:13');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (8, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">一入宫廷深似海</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝宫女们一经选入宫内，便失去自由，大都是衣食菲薄，住所简陋，终身苦役，不能与父母相见。繁琐的礼节，森严的等级，不时的凌辱，几乎无出头之日。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">１、“提铃”和“板着”之罚。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">宫女们是被严格控制的。她们除了完成各种苦役之外，她们还要经常在知书女内官的教习下读《女训》、《女孝经》等书以扭曲她们的心灵。宫女稍有违规者，将被处以“墩锁”“提铃”和“板着”。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">“提铃”就是受罚宫女每夜自明宫德乾清宫门到日精门、月华门，然后回到乾清宫前。徐行正步，风雨不阻，高唱天下太平，声援而长，与铃声相应，这是一般的。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">“板着”就是受罚宫女面向北方立定，弯腰伸出双臂来，用手扳住两脚。不许身体弯曲，一直要持续一个时辰，即两个小时左右，一般情况是受罚宫女必定头晕目眩，僵仆卧地，甚有呕吐成疾，至殒命的。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">２、宫女生病无医，自生自灭。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝规定：“宫嫔以下有疾，医者不得入，以证取药。”宫嫔尚且如此，宫女自不待言。据《明宫史》载：</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在金鳌玉桥西、棂星门迤北羊房夹道（今名养蜂夹道），有内安乐堂，“有掌司总其事者二三十人。凡宫人病老或有罪，先发此处，待年久方再发外之浣衣局也。”</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这就是说，宫人得了病，或是年老了，要和有罪的人一样，发到这里，靠自己的生命力延续时日，或者等死。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">如果少数人偶被皇帝看中，地位略有改变，生得子女者尚能晋封，否则也只能幽闭深宫，了此一生。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">３、死无葬所，火烧后尸灰填入枯井。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝宫女一生锁闭深宫，供帝王玩弄、使役到死亡（包括各种死亡）后，如果不是有名的，都不会赐墓，而是火葬。火烧后将众多尸灰一起填入枯井。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">到了嘉靖年间，有一个贵嫔捐钱买了几亩民地，宫女不愿其尸灰入井的，则埋此地中。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据清人刘廷玑《在园杂志》卷三说：“墙固垒垒，碑亦林立，……每于风雨之夜，或现形，或作声，幽魂不散。”</span>\r\n</p>', 4, 2, '', '2015-11-23 23:45:22', '2015-11-23 23:50:07');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (9, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">残忍变态的“性虐待”</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在中国历史上，隋炀帝的荒淫无耻臭名昭著，然而明朝很多帝王更比隋炀帝厉害，他们的糜烂、淫乱更上一层楼。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据史记载，自宪宗至熹宗160多年间(1465～1627年)，皇帝和大臣见面的次数约略可数。宪宗在位23年，仅召见大学士万安等一次，只说了几句话就在万安高呼万岁之声中退朝了。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">武宗在位16年，一次也没有召见大臣。世宗、神宗在位都达四五十年，都是20多年不视朝政，都忙于淫乐，其间不知道性病有多少宫女遭受过各种残酷的“性虐待”。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明人笔记中记有神宗与宫女淫乐之事说：</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">夏日，明月高悬之夜，与后宫嬉。令人自轻罗制成之囊中，放出流萤无数。再令宫女以轻罗团扇争相扑捉，若流萤落于谁簪上，则是夜帝必幸之。故宫女争以香水洒于簪上，以待流萤。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">秋日，帝题唐人王建宫词前二句于红叶上，令宫女题该词后二句于另一红叶，一起放入御沟，若遇两叶相叠，令人取观，如成全首宫词，则书后二句之宫女，是夜必获帝幸。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">冬日，于洛阳殿大池，注满香汤，挑选柔肌雪肤之宫女，同浴于池，效鸳鸯戏水之乐。浴罢，则坐于锦绣上拥美饮酒，谓之“鸳鸯之会”。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-8.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">荒淫的明武宗让当时的大太监刘瑾设置豹房。豹房就是修建的特定建筑物，内设宫女和从外强来的民女等供帝王淫乐的场所。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明武宗在内淫乐不够瘾，大肆对宫女“性虐待”，传说，还有强迫宫女与兽类交媾的事等，其“性虐待”之变态和残酷啊。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-9.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', 4, 3, '', '2015-11-23 23:50:16', '2015-11-23 23:54:15');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (10, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">残酷而悲怆的“殉葬”</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明朝在中国历史上把变态残忍的殉葬之风推向又一个高潮，妃嫔殉葬成为明初封建统治的一种制度(到明英宗朱祁镇时被废止)，而且民间也深受影响。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">明代皇帝死亡后，一些绝美的宫女，是被钦点“殉葬”的。据有关史料记载：朱元璋死以后有40个妃嫔殉葬，朱棣死时有30多妃嫔殉葬，明仁宗朱高炽(朱棣的儿子)执政不到一年，葬礼极为简略，但是也有 7个妃嫔殉葬，明宣宗朱瞻基有10个妃嫔殉葬等。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">《明史·后妃传》记载一个民间少女郭爱被选入宫中仅20天，朱瞻基死了，她得知自己被列入殉葬名单之后，含泪写下一首绝命词：“修短有数兮，不足较也;生而如梦兮，死则觉也;失吾亲而归兮，不足较也;惭余之不孝也，心凄凄而莫能已兮，则可悼也。”</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">《朝鲜李朝实录中的中国史料》记载明成祖朱棣死后明朝宫廷内逼宫人殉葬的情形：</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-10.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">等待殉葬的三十余名宫女吃完饭后，被引入一宫殿，凄惨的哭声震动宫殿。宫女们依次被送上木床，太监们将宫女的头颈按入丝带的套圈内，然后抽去木床，宫女在惨烈的挣扎中停止了更年期呼吸，一个个像伸着长长勃颈的雉鸡一样吊挂着。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">待宫女们死定后，仁宗(朱高炽)亲入“辞决”，“辞决”多么“谥美”的词藻啊。然后封这些怨寂的死尸为手术什么贞惠妃呀、贞丽妃呀、贞淑妃呀等等。这是多么的残忍和虚伪啊。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 黑体, SimHei;\">反抗与“凌迟”杀戮</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">幽闭深宫的宫女长期堪忍受着明朝皇帝们变态和残忍的虐待(包括肌体、苦役、体罚、性虐待等)。据史书记载，仅嘉靖帝笞楚虐待致死的宫女多达200余人。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">嘉靖二十一年，即公元1542年十月，饱受虐待，濒于绝境的10几名宫女以柔弱素手干出了一件古今中外都不曾有过的大事——勒杀嘉靖帝，此大案历史上称作“壬寅宫变”。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">当时，由于宫女缺乏经验，误拴死结，没有把嘉靖帝勒死。后来，这10几名宫女都被凌迟处死。所谓“凌迟”，就是人们常说的“千刀万剐”的酷刑。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">宫女地位的低下，文献夫妻上是从来不记载她们的名字的，惟独此案有一个被剐宫女的详细名单。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://localhost:8080/78SaySystem/upload/20151123/text-27-4-11.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">根据《明实录》记载：她们是杨金英、蓟川药、杨玉香、邢翠莲、姚淑翠、杨翠英、关梅秀、刘妙莲、陈菊花、王秀兰、张金莲、徐秋花、邓金香、张春景、黄玉莲。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这真是千古悲剧，万古绝杀啊!</span>\r\n</p>', 4, 4, '', '2015-11-23 23:54:24', '2015-11-23 23:58:57');
