INSERT INTO `Article` (`id`, `title`, `abstract`, `content`, `tag`, `logo`, `logo2`, `categoryId`, `source`, `views`, `reviews`, `editor`, `listType`, `isHeadlines`, `isPlacedHome`, `isFeatured`, `isPublish`, `isDelete`, `createTime`, `modifyTime`) VALUES (1, '全世界唯一一只白色座头鲸', '米伽罗是世界上唯一的白色雄性座头鲸。米伽罗是澳洲土著语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下', '2011年10月，一头通体全白的座头鲸在澳大利亚大堡礁附近出现，出生数周，大概有12英尺(约3.65米)长。这种纯白色座头鲸估计在澳大利亚东海岸水域仅有10到15头，出现几率仅为千分之一。目击者韦恩·费文思看见了座头鲸浮出水面的瞬间。“这是一辈子也轮不上的运气。”费文思说，“我们当时正在漂流，突然，我看见一只白色的小鲸鱼出现在海面上。我当时简直不敢相信自己的眼睛。”', '世界唯一,座头鲸,动物', 'upload/20151123/logo-22-1-1.jpg', NULL, 22, '米尔网', 28, 0, '青菜萝卜', 1, '', '', '', '', '', '2015-11-23 16:42:36', '2015-11-23 17:18:50');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (1, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">这只座头鲸可能是米伽罗，它被认为是全世界唯一一只白色座头鲸。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">米伽罗被认为是为了热带的昆士兰北部温暖海域而从南极迁徙到澳大利亚的东部海岸。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://192.168.1.198:9080/78SaySystem/upload/20151123/text-22-1-1.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据悉，这只饱受人们喜爱的座头鲸自1991年首度被海洋生物学家Dan Burns发现后，每年都会返回一次，今年已经是“米伽罗”往返迁徙的第23个年头。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">在博特尼湾鲸鱼工作站志愿者的提醒下，摄影爱好者有幸在现场捕捉到“米伽罗”与其他座头鲸嬉戏追逐的身影。</span>\r\n</p>\r\n<p style=\"text-align:center\">\r\n    <span style=\"font-family: 宋体, SimSun;\"><img src=\"http://192.168.1.198:9080/78SaySystem/upload/20151123/text-22-1-2.jpg\" style=\"width: 500px; height: 340px;\"/></span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">35岁的“米伽罗”因其通体呈罕见的白色而显得弥足珍贵，已经受到联邦法律的保护，以防止人们向它靠近。</span>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">当前，世界上与“米伽罗”一样珍稀的另一条白化巨鲸身处挪威。通常，座头鲸的迁徙从南极启程，抵达北昆士兰后，再于9月返回南极，这个迁徙过程大约持续3个月。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://192.168.1.198:9080/78SaySystem/upload/20151123/text-22-1-3.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', 1, 1, '', '2015-11-23 16:55:25', '2015-11-23 17:10:33');
INSERT INTO `Chapter` (`id`, `content`, `articleId`, `sort`, `isDelete`, `createTime`, `modifyTime`) VALUES (2, '<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据英国媒体报道，最受澳大利亚人们喜爱的白化座头鲸“米伽罗”（Migaloo）最近一次现身是在去年新南威尔士的格林角海岸，这令当地民众惊喜不已。随后的几天，“米伽罗”又数次出现在悉尼的Cronulla海滩，让人大饱眼福。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://192.168.1.198:9080/78SaySystem/upload/20151123/text-22-1-4.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">米伽罗（Migaloo）是澳洲土着语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下。2013年9月，摄影师在澳大利亚昆士兰北部的埃蒂海湾拍摄到了一只全身白色的座头鲸。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://192.168.1.198:9080/78SaySystem/upload/20151123/text-22-1-5.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">白色雄性座头鲸。米伽罗（Migaloo）是澳洲土着语“白色”的意思。它在1991年首次在澳大利亚水域被世人发现。直到2009年，米伽罗才再次在澳大利亚海域附近出现，爱护动物的人士终于松一口气，因为米伽罗并没有死在日本捕鲸船的“魔掌”之下。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://192.168.1.198:9080/78SaySystem/upload/20151123/text-22-1-6.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>\r\n<p style=\"line-height: 1.75em; text-indent: 2em; text-align: left;\">\r\n    <span style=\"font-family: 宋体, SimSun;\">据生物学家的最新研究发现，座头鲸用以交流的“歌声”中包含有人类语言要素。不过尽管研究者认为鲸在本质上尚未拥有自己的语言，但他们还是发现了座头鲸歌声与人类语言之间的相似之处。</span>\r\n</p>\r\n<p style=\"text-align: center;\">\r\n    <img src=\"http://192.168.1.198:9080/78SaySystem/upload/20151123/text-22-1-7.jpg\" style=\"width: 500px; height: 340px;\"/>\r\n</p>', 1, 1, '', '2015-11-23 17:16:35', '2015-11-23 17:16:38');
